require_once APPPATH . 'controllers/admin/base/base_admin_controller.php';
class xAdmin_SiteOrderStatus extends Base_Admin_Controller {

  /**
   * Implementation of POST_SAVE event callback
   * @param Object $entity reference
   * @return Object
   */
  protected function postSave(&$entity) {
    if (!empty($entity['alert_str_time'])) {
      $time = strtotime($entity['alert_str_time']);
      if ($time === FALSE) {
        set_flash_error('Синтаксическая ошибка во временной переменной');
        redirect_to_referral();
      }
      if ($time < time()) {
        set_flash_error('Логическая ошибка во временной переменной');
        redirect_to_referral();
      }
    }

    return $entity;
  }

}