$.fn.placeholder=function(){if(this[0]&&"placeholder" in document.createElement("input")){return}return this.each(function(){$(this).focus(function(){var a=$.trim($(this).val());if(a===$(this).attr("placeholder")){$(this).val("")}}).blur(function(){var a=$.trim($(this).val());if(a===""){$(this).val($(this).attr("placeholder"))}});if($(this).attr("placeholder")&&$.trim($(this).val())==""){$(this).val($(this).attr("placeholder"))}})};$(document).ready(function(){$("input[placeholder]").each(function(){$(this).placeholder();var a=this;$(this).parents("form:first").bind("submit",function(){if($(a).val()==$(a).attr("placeholder")){$(a).val("")}})})});